package service;

import model.Employee;

import java.util.ArrayList;
import java.util.List;

public class EmployeeService {

    public static List<Employee>  employeeList = new ArrayList<>();

    static {
        employeeList.add(new Employee(1,"A","123456789","data:image/jpeg;base64,"));
        employeeList.add(new Employee(2,"B","123456789","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcStHsV-NxS1Gzt2qugMTwz3Hg1NymvpTGywHw&usqp=CAU"));
        employeeList.add(new Employee(3,"C","123456789","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRW8HSI-509RZeh3xzzRQTQdK9kOsdkyrBa5Q&usqp=CAU"));
        employeeList.add(new Employee(4,"D","123456789","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSuuwP20zh5fwAZ0O19VVo4DKqUlImnSDUR6Q&usqp=CAU"));
        employeeList.add(new Employee(5,"E","123456789","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSo5dGTGaTXbOTSKC0g-I3BRBKfMDv3wP3Bxg&usqp=CAU"));
    }

    public static List<Employee> findAll(){
        return employeeList;
    }

    public static void add(Employee employee){
        employeeList.add(employee);
    }

    public static void delete(int id){
        employeeList.remove(findId(id));
    }

    public static void edit(int id,Employee employee){
        employeeList.set(findId(id),employee);
    }

    public static int findId(int id){
        int index = -1;
        for (int i = 0; i < employeeList.size(); i++) {
            if (employeeList.get(i).getId() == id){
                index = i;
            }
        }
        return index;
    }

    public static Employee findEmployee(int id){
        for (Employee em :
                employeeList) {
            if (em.getId() == id) {
                return em;
            }
            }
        return null;
    }
}
